import React from 'react';
import { Formik, Field, Form, FieldArray, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { Debug } from "./Debug";
import './App.css';

const initialValues = {
  friends: [
    {
      name: '',
      email: '',
    },
  ],
};

const App = () => (
  <div className="container">
    <div
      className="m-auto bg-white rounded px-10 py-10 shadow-lg"
      style={{ width: 600 }}
    >
      <h1 className="text-4xl font-bold mb-5">Invite friends</h1>
      <Formik
        initialValues={initialValues}
        validationSchema={Yup.object({
          friends: Yup.array()
            .of(
              Yup.object({
                name: Yup.string().required('Required'),
                email: Yup.string()
                  .email('Invalid email')
                  .required('Required'),
              })
            )
            .min(3, 'Must invite at least three friends'),
        })}
        onSubmit={(values, actions) => {
          setTimeout(() => {
            actions.setSubmitting(false);
            alert(JSON.stringify(values, null, 2));
          }, 2000);
        }}
      >
        {({ values, errors, isSubmitting }) => {
          return (
            <Form>
              <FieldArray name="friends">
                {({ push, remove }) => (
                  <React.Fragment>
                    {values.friends &&
                      values.friends.length > 0 &&
                      values.friends.map((_friend, index) => (
                        <div key={index} className="col-sm-3">
                          <div className="col-sm-3">
                            <Field
                              name={`friends[${index}].name`}
                              type="text"
                              placeholder="Jane Doe"
                              className="px-2 py-3 mr-3 border-solid border-grey-light border-2 rounded"
                            />
                            <ErrorMessage name={`friends[${index}].name`}>
                              {msg => (
                                <div className="p-2 text-red-dark">{msg}</div>
                              )}
                            </ErrorMessage>
                          </div>
                          <div className="col-sm">
                            <Field
                              name={`friends[${index}].email`}
                              type="email"
                              placeholder="jane@example.com"
                              className="px-2 py-3 mr-3 border-solid border-grey-light border-2 rounded"
                            />
                            <ErrorMessage name={`friends[${index}].email`}>
                              {msg => (
                                <div className="p-2 text-red-dark">{msg}</div>
                              )}
                            </ErrorMessage>
                          </div>
                          <button
                            type="button"
                            onClick={() => remove(index)}
                            className="w-8 h-8 border-solid border-grey-light border-2 text-grey-dark rounded"
                            style={{ marginTop: 7 }}
                          >
                            X
                          </button>
                        </div>
                      ))}

                    <button
                      type="button"
                      onClick={() => push({ name: '', email: '' })}
                      className="w-full p-3 mb-3 text-xl uppercase font-bold shadow-md rounded"
                    >
                      Add Friend
                    </button>
                  </React.Fragment>
                )}
              </FieldArray>
              <button
                type="submit"
                disabled={isSubmitting}
                className={`w-full p-3 mb-3 text-xl uppercase font-bold shadow-md rounded text-black ${
                  isSubmitting ? 'bg-grey' : 'bg-green'
                }`}
              >
                Invite
              </button>

              <div className="p-2 text-red-dark text-center">
                {typeof errors.friends === 'string' ? errors.friends : null}
              </div>
              <Debug />
            </Form>
          );
        }}
      </Formik>
    </div>
  </div>
);

export default App;
